def employer_bulletpoints(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard = True, row_width=1)
    item_1 = types.KeyboardButton('üí∞ –õ—å–≥–æ—Ç–Ω—ã–µ –Ω–∞–ª–æ–≥–∏')
