    item_3 = types.KeyboardButton('üí≥ –õ—å–≥–æ—Ç—ã –ø–æ –∫—Ä–µ–¥–∏—Ç–∞–º')
    item_4 = types.KeyboardButton('‚ûï –û—Å—Ç–∞–ª—å–Ω—ã–µ –ª—å–≥–æ—Ç—ã')
    back_to_select_specialization = types.KeyboardButton('üîô –ü–µ—Ä–µ–≤—ã–±—Ä–∞—Ç—å —Å–ø–µ—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—é')
