def credit_business_type(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard = True, row_width=1)
    item_1 = types.KeyboardButton('‚ñ™Ô∏è –ú–∞–ª—ã–π –±–∏–∑–Ω–µ—Å –∏ —Å–∞–º–æ–∑–∞–Ω—è—Ç—ã–µ')
    item_2 = types.KeyboardButton('‚óæÔ∏è –°—Ä–µ–¥–Ω–∏–π –±–∏–∑–Ω–µ—Å')
